import EventManager from '../lib/EventManager';

const toastEventManager = new EventManager();

export default function toast({ type, text }) {
  const event = new CustomEvent('addtoast', {
    detail: {
      type,
      text,
    },
  });

  document.dispatchEvent(event);
}
