export default class EventManager {
  constructor() {
    this.listeners = {};
  }

  on(event, listener) {
    if (!this.listeners[event]) {
      this.listeners[event] = [];
    }

    this.listeners[event].push(listener);
  }
}

const toastManager = new EventManager();

toastManager.on('addtoast', () => {});
console.log(toastManager);
