import styled from 'styled-components';

export const Container = styled.div`
  margin-top: 32px;
`;

export const Header = styled.header`
  strong {
    color: #222;
    font-size: 24px;
  }

  a {
    color: #5061FC;
    text-decoration: none;
    font-weight: bold;
    border: 2px solid #5061FC;
    padding: 8px 16px;
  }
`;
